@Composable
fun CustomVectorDrawableSample() {

    var index by remember {
        mutableStateOf(0f)
    }

    Column {
        Netherlands(
            modifier = Modifier
                .background(Blue400)
                .fillMaxWidth()
                .aspectRatio(1f),
            index.toInt()
